import { FETCHDATA } from './actions'

const initialState = {
    data:[]
}

export default (state = initialState, { type, payload }) => {
    switch (type) {

    case FETCHDATA:
        return {
            ...state,
            data: payload.result
        }

    default:
        return state
    }
}
