import { call, put, takeEvery} from 'redux-saga/effects'

import { 
    FETCH_DATA_SUCCESS, 
    FETCH_DATA_FAILURE,  
    FETCH_DATA_INIT
    } from './actions'

import Api from '../lib/services'

function* fetchData(payload) {
    try {
        const result =  yield call(Api.fetchData, payload)
        yield put(FETCH_DATA_SUCCESS, result)

    } catch (error) {
       yield put(FETCH_DATA_FAILURE())
    }

}

function* watchSaga() {
    yield takeEvery(FETCH_DATA_INIT, fetchData)
  }

export default watchSaga
